settings.custom_liquid:
{%- comment -%}
  Dismissible Promotional Banner
  - Shows a message that can be closed by the user.
  - Uses sessionStorage to keep it closed for the current browser session.
{%- endcomment -%}

{%- assign banner_id = "promo-banner-" | append: section.id | default: "global" -%}

<div id="{{ banner_id }}" class="promo-banner">
  <span class="promo-banner__text">
    Free shipping on orders over $300
  </span>
  <button id="{{ banner_id }}__close" class="promo-banner__close" aria-label="Dismiss this message" title="Dismiss">
    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2.5" stroke-linecap="round" stroke-linejoin="round">
      <line x1="18" y1="6" x2="6" y2="18"></line>
      <line x1="6" y1="6" x2="18" y2="18"></line>
    </svg>
  </button>
</div>

<style>
  .promo-banner {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 12px 20px;
    margin: 12px 0; /* Updated margin top/bottom to 12px */
    background-color: #f8f2e9; /* Light tan to align with #c19967 */
    color: #5c4730; /* Darker brown for text contrast */
    border-radius: 8px; /* Corner radius */
    font: 400 15px/1.4 -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Arial, sans-serif;
  }
  .promo-banner__close {
    background: none;
    border: none;
    padding: 0;
    margin-left: 16px;
    cursor: pointer;
    line-height: 0; /* Ensures the icon aligns well */
    opacity: 0.6;
    transition: opacity 0.2s ease-in-out;
  }
  .promo-banner__close:hover {
    opacity: 1;
  }
  .promo-banner__close svg {
    color: #5c4730; /* Updated icon color */
    display: block;
  }
</style>

<script>
(function() {
  const bannerId = '{{ banner_id }}';
  const closeButtonId = bannerId + '__close';
  const storageKey = 'promoBannerClosed_' + bannerId;

  const banner = document.getElementById(bannerId);
  const closeButton = document.getElementById(closeButtonId);

  if (!banner || !closeButton) {
    return;
  }

  // Check if the banner was already closed during this session
  if (sessionStorage.getItem(storageKey) === 'true') {
    banner.style.display = 'none';
    return;
  }

  // Listen for a click on the close button
  closeButton.addEventListener('click', function() {
    banner.style.display = 'none';

    // Remember the closed state for this session only
    try {
      sessionStorage.setItem(storageKey, 'true');
    } catch (e) {
      console.error('sessionStorage is unavailable or full:', e);
    }
  });
})();
</script>
