settings.custom_liquid:
{%- comment -%}
  AFTERPAY MINI-BANNER â€” Cart Version
  Shows: "4 payments of {cart subtotal / 4} with [Afterpay logo]
          Learn more"
{%- endcomment -%}

{%- assign _per_payment = cart.total_price | times: 0.25 -%}

<div id="afterpay-cart-banner" class="ap-mini-banner" role="note" aria-label="Afterpay instalments">
  <span class="ap-text">
    4 payments of
    <strong class="ap-amount">
      {{ _per_payment | money | replace: "A$", "$" }}
    </strong>
    with
  </span>

  <a class="ap-logo" href="https://www.lestyle.com.au/blogs/lestyle-spotlight/buy-now-pay-later-with-afterpay/" target="_blank" rel="noopener" aria-label="About Afterpay">
    <img src="https://cdn.shopify.com/s/files/1/0758/6467/0467/files/afterpay_logo.png?v=1755220716" alt="afterpay" loading="lazy">
  </a>

  <div class="ap-learn-wrapper">
    <a class="ap-learn" href="https://www.lestyle.com.au/blogs/lestyle-spotlight/buy-now-pay-later-with-afterpay/" target="_blank" rel="noopener">Learn more</a>
  </div>
</div>

<style>
  .ap-mini-banner{
    display:flex; flex-wrap:wrap; align-items:center; gap:.5rem;
    font: 400 14px/1.4 -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Arial, sans-serif;
    color:#111;
    margin: 0 0 15px 0; /* Left/Right: 15px, Bottom: 20px */
  }
  .ap-mini-banner .ap-text{white-space:nowrap;}
  .ap-mini-banner .ap-amount{font-weight:700;}
  .ap-mini-banner .ap-logo img{
    height:26px; /* Bigger logo */
    width:auto;
    display:block;
  }
  .ap-mini-banner .ap-learn-wrapper{
    flex-basis:100%;
  }
  .ap-mini-banner .ap-learn{
    text-decoration:underline;
    color:#111;
    font-size:14px;
  }
</style>

<script>
(function() {
  const container = document.getElementById('afterpay-cart-banner');
  if (!container) return;

  const amountEl = container.querySelector('.ap-amount');

  function formatMoneyFromCents(cents) {
    try {
      return (cents/100).toLocaleString(undefined, { style: 'currency', currency: "AUD" }).replace(/^A\$/, '$');
    } catch(e) {
      return '$' + (cents/100).toFixed(2);
    }
  }

  function updateFromCart() {
    fetch('{{ routes.cart_url }}.js')
      .then(res => res.json())
      .then(cart => {
        const perPaymentCents = Math.round(cart.total_price / 4);
        amountEl.textContent = formatMoneyFromCents(perPaymentCents);
      });
  }

  // Initial load
  updateFromCart();

  // Theme events
  document.addEventListener('cart:updated', updateFromCart);
  document.addEventListener('cart:change', updateFromCart);

  // Fallback for AJAX fetch
  const origFetch = window.fetch;
  window.fetch = function(...args) {
    return origFetch.apply(this, args).then(res => {
      if (args[0] && typeof args[0] === 'string' && args[0].includes('/cart')) {
        updateFromCart();
      }
      return res;
    });
  };
})();
</script>
