{% comment %}
  A standalone search form component.
  It includes the necessary HTML form structure and inline CSS for styling.
{% endcomment %}

<style>
  .search-form__container {
    margin: 2rem auto;
    padding: 0 1rem;
    max-width: 600px;
    width: 100%;
  }

  .search-form__wrapper {
    position: relative;
    display: flex;
    width: 100%;
    align-items: center;
  }

  .search-form__input {
    width: 100%;
    height: 44px;
    padding: 10px 50px 10px 15px; /* Right padding to avoid text overlapping with button */
    border: 1px solid #c9c9c9;
    /* MODIFIED: Changed border-radius to 6px */
    border-radius: 6px;
    font-size: 1rem;
    -webkit-appearance: none;
    transition: border-color 0.2s ease-in-out;
  }

  .search-form__input:focus {
    border-color: #333;
    outline: none;
  }

  .search-form__button {
    position: absolute;
    right: 5px;
    top: 50%;
    transform: translateY(-50%);
    height: 38px;
    width: 38px;
    padding: 0;
    margin: 0;
    background: transparent;
    border: none;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    color: #555;
  }

  .search-form__button:hover {
    color: #000;
  }

  .search-form__button svg {
    width: 18px;
    height: 18px;
  }
</style>

<div class="search-form__container">
  <form action="{{ routes.search_url }}" method="get" role="search" class="search-form__wrapper">

    {% comment %} These hidden inputs improve search accuracy {% endcomment %}
    <input type="hidden" name="options[unavailable_products]" value="last">
    <input type="hidden" name="options[prefix]" value="last">
    <input type="hidden" name="options[fields]" value="title,vendor,product_type,variants.title">

    <input
      type="search"
      name="q"
      value="{{ search.terms | escape }}"
      required
      autocomplete="off"
      placeholder="{{ 'general.search.placeholder' | t }}"
      aria-label="{{ 'general.search.placeholder' | t }}"
      class="search-form__input"
    >

    <button type="submit" class="search-form__button" aria-label="{{ 'general.search.submit' | t }}">
      <svg
        fill="currentColor"
        stroke="currentColor"
        xmlns="http://www.w3.org/2000/svg"
        viewBox="0 0 512 512"
      >
        <path d="M508.5 468.9L387.1 347.5c-2.3-2.3-5.3-3.5-8.5-3.5h-13.2c31.5-36.5 50.6-84 50.6-136C416 93.1 322.9 0 208 0S0 93.1 0 208s93.1 208 208 208c52 0 99.5-19.1 136-50.6v13.2c0 3.2 1.3 6.2 3.5 8.5l121.4 121.4c4.7 4.7 12.3 4.7 17 0l22.6-22.6c4.7-4.7 4.7-12.3 0-17zM208 368c-88.4 0-160-71.6-160-160S119.6 48 208 48s160 71.6 160 160-71.6 160-160 160z"/>
      </svg>
    </button>
  </form>
</div>

